alter table "public"."posts_tags" add constraint "posts_tags_tag_id_fkey" FOREIGN KEY (tag_id) REFERENCES tags(id) ON DELETE CASCADE not valid;

alter table "public"."posts_tags" validate constraint "posts_tags_tag_id_fkey";

alter table "public"."tags" add constraint "tags_user_id_fkey" FOREIGN KEY (user_id) REFERENCES users(id) not valid;

alter table "public"."tags" validate constraint "tags_user_id_fkey";

